import React from 'react'
import FacebookLogin from 'react-facebook-login/dist/facebook-login-render-props'
//import { GoogleLogin } from 'react-google-login';
import GitHubLogin from 'react-github-login';
import '.././css/signinform.css'
import axios from 'axios';

import { useDispatch, useSelector } from 'react-redux';
import {
    selectLoginState,
    selectAccessTokenState
} from './reducers/articlesReducer';

import { 
    setLogin,
    setLogout,
    setAccessToken
} from './actions/articlesAction';


const SignInForm = () => {
    const disPatch = useDispatch();

    const passAccessToken = async (provider, access_token) => {
        const res = await axios.post('/api/social-auth/', {
            provider: provider,
            access_token: access_token
        });
        return res;
    }

    const responseFacebook = async response => {
        try {
            console.log(response["accessToken"])
            const res = await passAccessToken('facebook', response["accessToken"]);
            console.log(res);
            disPatch(setLogin());
            disPatch(setAccessToken(res));
        } catch (error) {
            //res.data = error;
            console.log(error);
            disPatch(setLogout());
        }
    }

    const responseGoogle = (response) => {
        console.log(response);
    }


    const ResponseGithubOnSuccess = async response => {
        try {
            const res = await axios.post('api/get-github-access-token/', {
                code: response['code']
            });
            const res2 = await passAccessToken('github', res['data']);
            console.log(res2);
            disPatch(setLogin());
            disPatch(setAccessToken(res2));
        } catch (error) {
            //res.data = error;
            console.log(error);
            disPatch(setLogout());
        }
    }

    const ResponseGithubOnFailure = response => console.error(response);  

    return (
        <div className="signinform">
            <FacebookLogin
                cssClass="fb-btn"
                appId="240314257268798"
                autoLoad={false}
                fields="name,email,picture"
                callback={responseFacebook}
                render={renderProps => (
                    <button onClick={renderProps.onClick} className="fb-btn">Facebook</button>
                  )}/>
            <GitHubLogin 
                className="github-btn"
                clientId="51b1a8ee5b7cad1e6a85"
                redirectUri="http://localhost:3000/signin" 
                onSuccess={ResponseGithubOnSuccess}
                onFailure={ResponseGithubOnFailure}
                buttonText="Github"/>
        </div>

    )
}

export default SignInForm
