import { Action } from 'redux';
import { RootState} from '../store/store'
import axios from 'axios';
//Types
import { GET_ARTICLES } from '../types'

//Actions
interface GetArticlesAction {
    readonly type: typeof GET_ARTICLES
    readonly payload: Array<object>
}

export const getArticles = async (): Promise<GetArticlesAction> => {
    try {
        const res = await axios.get('/api/articles');
        return {
            type: GET_ARTICLES, 
            payload: res.data}
    }
    catch (err) {}
}

//Selector functions
export const selectArticlesState = (rootState: RootState) => rootState.articlesReducer;

//Initial state
const initialState = {
    articles: [],
}

//Reducer
interface ArticlesState {
    articles: Array<object>;
}

const articlesReducer = (state: ArticlesState = initialState, action: GetArticlesAction) => {
    switch(action.type) {
        case GET_ARTICLES:
            return {
                ...state,
                products: action.payload
            }
        default:
            return state; 
    }
};

export default articlesReducer;
