//Selector functions
export const selectArticlesState = (rootState: RootState) => rootState.articlesReducer;

//Initial state
const initialState = {
    articles: [],
}

//Reducer
interface ArticlesState {
    articles: Array<object>;
}

const articlesReducer = (state: ArticlesState = initialState, action: GetArticlesAction) => {
    switch(action.type) {
        case GET_ARTICLES:
            return {
                ...state,
                articles: action.payload
            }
        default:
            return state; 
    }
};

export default articlesReducer;
