import React from 'react'
import { Dropdown, DropdownButton} from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { 
    setCategory,
    clearCategory,
    resetLoading
} from './actions/articlesAction';
import Router, { useRouter } from 'next/router'

const ArticleDropdown = () => {
    const disPatch = useDispatch();
    const router = useRouter()
    const handleSelect=(e)=>{
        if (e != "-1") {
            console.log(e);
            disPatch(setCategory(e));
            if (router.pathname !== '/') {
                Router.push(`/`)
            }
        } else {
            disPatch(clearCategory());
        }
    }

    return (
        <DropdownButton className='article-dropdown' id="dropdown-basic-button" title="Topics" onSelect={handleSelect}>
            <Dropdown.Item eventKey="-1">All</Dropdown.Item>
            <Dropdown.Item eventKey="0">Challenges</Dropdown.Item>
            <Dropdown.Item eventKey="1">Programming</Dropdown.Item>
        </DropdownButton>
    )
}

export default ArticleDropdown
