import React, {useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux';
import {
    selectArticlesState,
    selectFilteredArticlesState,
    getArticles } from './reducers/articlesReducer';
import {
    CSSTransition,
    TransitionGroup,
  } from 'react-transition-group';
import Article from './Article'
import 'bootstrap/dist/css/bootstrap.min.css';

const ArticleList = () => {
    const disPatch = useDispatch();
    const articles = useSelector(selectArticlesState);
    const filteredArticles = useSelector(selectFilteredArticlesState);
    useEffect(()=> {
        disPatch(getArticles());
    }, [])

    useEffect(()=> {
        //console.log(articles)
    }, [articles])

    useEffect(()=> {
        console.log(filteredArticles)
    }, [filteredArticles])

    return (
        <div className="container menu-container">
            <div className="row">
            {filteredArticles.length !== 0 ? filteredArticles.map(filteredArticle=>(
                    <TransitionGroup>
                        <CSSTransition  timeout={500} classNames="item">
                            <Article key={filteredArticle.id} article = {filteredArticle}/>
                        </CSSTransition>
                    </TransitionGroup>)
            ): articles.map(article=>(
                <TransitionGroup>
                    <CSSTransition key={article.id} timeout={500} classNames="item">
                        <Article article = {article}/>
                        </CSSTransition>
                    </TransitionGroup>))
            }
            </div>
        </div>
    )
}

ArticleList.getInitialProps = async () => {
    let news;
    try {
        const disPatch = useDispatch();    
    } catch (err) {
      news = [];
    }

    return {
        news
    }
  }
export default ArticleList;
