import axios from 'axios'; //Types

import { GET_ARTICLES, GET_FILTERED_ARTICLES, CLEAR_FILTER } from '../types'; //Actions

export const getArticles = () => async dispatch => {
  const res = await axios.get('/api/articles?category=1');
  console.log(res.data);
  await dispatch({
    type: GET_ARTICLES,
    payload: res.data
  });
};
export const getFilteredArticles = text => dispatch => {
  dispatch({
    type: GET_FILTERED_ARTICLES,
    payload: text
  });
};
export const clearFilter = () => dispatch => {
  dispatch({
    type: CLEAR_FILTER
  });
};
