import React, {useEffect, useState} from 'react'
import PropTypes from 'prop-types'
import { Card, Button} from 'react-bootstrap';
import ArticleModal from '../components/ArticleModal';
import '.././css/article.css'
import '.././css/card.css'

const Article = ({article}) => {
    const [show, setShow] = useState(false);

    const handleClose = () => {
        setShow(false);
    }

    const handleShow = () => {
        setShow(true);
    }

    useEffect(()=> {
        console.log(article)
    }, [])

    return (
        <div className="col-md-6 col-xs-6">
            <Card className="article-card">
                <Card.Body>
                    <img className="article-card" src={`${article.image}`} alt={article.title}/>
                    <div className="title">{article.title}</div>
                    <div className="date">{article.time}</div>
                    <div className="readtime">{article.readtime}</div>
                    <button className="article-btn" onClick = {handleShow}>View this topic</button>
                </Card.Body>
            </Card>
            <ArticleModal show = {show} handleClose = {handleClose} article = {article} />
        </div>

    )
}

Article.propTypes = {
    article: PropTypes.object.isRequired
}

export default Article
