import { Action } from 'redux';
import { RootState} from '../store/store'
import axios from 'axios';
//Types
import { GET_ARTICLES, GET_FILTERED_ARTICLES, ArticleType } from '../types'

//Actions

export interface GetArticlesAction {
    readonly type: typeof GET_ARTICLES
    readonly payload: Array<ArticleType>
}

export interface GetFilteredArticlesAction {
    readonly type: typeof GET_FILTERED_ARTICLES
    readonly payload: string
}

/*
export interface FetchTasksAction extends Action<'GET_ARTICLES'> {
    payload: ArticleType[];
}
*/

export const getArticles = () => async dispatch => {
        const res = await axios.get('/api/articles?category=1');
        console.log(res.data)
        await dispatch({
            type: GET_ARTICLES, 
            payload: res.data})
}

export const getFilteredArticles = text => async dispatch => {
    await dispatch({
        type: GET_FILTERED_ARTICLES, 
        payload: text})
}

//Selector functions
export const selectArticlesState = (rootState: RootState) => rootState.articlesReducer.articles;
export const selectFilteredArticlesState = (rootState: RootState) => rootState.articlesReducer.filteredArticles;

//Initial state
const initialState = {
    articles: [],
    filteredArticles: []
}

//Reducer
interface ArticlesState {
    articles: Array<ArticleType>;
    filteredArticles: Array<ArticleType>;
}

const articlesReducer = (state: ArticlesState = initialState, action: GetArticlesAction | GetFilteredArticlesAction) => {
    console.log(action.type)
    switch(action.type) {
        case GET_ARTICLES:
            return {
                ...state,
                articles: action.payload
            }
        case GET_FILTERED_ARTICLES:
            return {
                ...state,
                filteredArticles: state.articles.filter(article => {
                    const regex = new RegExp(`${action.payload}`, 'gi');
                    return article.title.match(regex) || article.subtitle.match(regex);
                })
            }
        default:
            console.log(123)
            return state; 
    }
};

export default articlesReducer;
