import React, {useEffect, Fragment, useState} from 'react'
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import {
    selectShowPlannerState,
    selectArticlesState
} from './states/states';

import {
    setLogout,
    getArticles,
    setBookmarks,
    setUsername,
    setAccessToken,
    setEmail,
    setProvider,
    setFinishedArticles,
    setUserId
} from './actions/articlesAction';

import {
    selectBookmarksState,
    selectFinishedArticlessState
} from './states/states';

import Article from './Article'
import {Spinner} from 'react-bootstrap';
import Footer from '../components/Footer';
import Router, { useRouter } from 'next/router'
import { VictoryPie } from 'victory';

const AchievementsContent = () => {
    const disPatch = useDispatch();
    const [data, setData] = useState([])
    const showPlanner = useSelector(selectShowPlannerState);
    const articles = useSelector(selectArticlesState);
    const bookmarks = useSelector(selectBookmarksState);
    const finishedArticles = useSelector(selectFinishedArticlessState);

    useEffect(()=> {
        //Fetch articles
        if (articles.length === 0) {
            console.log('fetch articles')
            disPatch(getArticles());
        }

    }, [])

    useEffect(()=> {
        //Fetch articles
        var dataList = []
        if (articles.length !== 0) {
            dataList.push({ x: `Remained (${articles.length})`, y: articles.length })
        }
        if (finishedArticles.length !== 0) {
            dataList.push({ x:  `Completed (${finishedArticles.length})`, y: finishedArticles.length })
        }
        if (bookmarks.length !== 0) {
            dataList.push({ x: `Unfinished (${bookmarks.length})`, y: bookmarks.length })
        }
        setData(dataList)

    }, [articles, bookmarks, finishedArticles])

    return (
        <Fragment>
            <div className={`${showPlanner === true ? 'achievementspage-head' : 'achievementspage-head achievementspage-head-remove-left'}`}>
                <div className="title">
                    Your Achievements
                </div>
                <div className="subtitle">
                    Keep going
                </div>
            </div>
            <div className={`${showPlanner !== true ? 'achievementspage-hide-siderbar' : 'achievementspage-hide-siderbar articles-hide-siderbar-remove-left'}`}>
                <div className="achievementspage-content-grid-box">
                    <div className="achievementspage-piechart">
                    <VictoryPie
                        colorScale={["black", "blue", "green" ]}
                        data={[
                            { x: `Remained (${articles.length})`, y: articles.length },
                            { x:  `Completed (${finishedArticles.length})`, y: finishedArticles.length },
                            { x: `Unfinished (${bookmarks.length})`, y: bookmarks.length }
                        ]}
                        innerRadius={20}
                        labelRadius={30}
                        style={{ labels: { fill: "white", fontSize: 16, fontWeight: "bold" } }}
                    />
                    </div>
                </div>
            </div>
        </Fragment>
    )
}

export default AchievementsContent
