import React, {useEffect, Fragment, useState} from 'react'
import { useDispatch, useSelector } from 'react-redux';
import ReactPaginate from 'react-paginate';
import {
    selectArticlesState,
    selectPartialArticlesState,
    selectFilteredArticlesState,
    selectIsLoadingState
} from './reducers/articlesReducer';

import { 
    getArticles,
    setPartialArticles
} from './actions/articlesAction';

import {
    CSSTransition,
    TransitionGroup,
} from 'react-transition-group';

import {Spinner} from 'react-bootstrap';
import Article from './Article'
import 'bootstrap/dist/css/bootstrap.min.css';
import Footer from '../components/Footer';

const ArticleList = () => {
    const [perPage, setPerPage] = useState(10);
    const [pageCount, setPageCount] = useState(0);
    const [offset, setOffset] = useState(0);
    const disPatch = useDispatch();
    const articles = useSelector(selectArticlesState);
    const filteredArticles = useSelector(selectFilteredArticlesState);
    const partialArticles = useSelector(selectPartialArticlesState);
    const isLoading = useSelector(selectIsLoadingState);

    useEffect(()=> {
        disPatch(getArticles());

    }, [])

    useEffect(()=> {
        //console.log(articles)
        const partialData = filteredArticles.length === 0 ? 
        articles.slice(offset, offset + perPage).map(article => {
           return  <Article  article = {article}/>
        }) : filteredArticles.slice(offset, offset + perPage).map(article => {
            return  <Article  article = {article}/>
        });
        console.log(partialData)
        setPageCount(filteredArticles.length === 0 ? Math.ceil(articles.length/perPage) : Math.ceil(filteredArticles.length/perPage));

        disPatch(setPartialArticles(partialData));
    }, [articles])

    useEffect(()=> {
        console.log(filteredArticles)
    }, [filteredArticles])

    const setPartialData = () =>  {
        const partialData = filteredArticles.length === 0 ? articles.slice(offset, offset + perPage) : filteredArticles.slice(offset, offset + perPage);
        setPageCount(filteredArticles.length === 0 ? Math.ceil(articles.length/perPage) : Math.ceil(filteredArticles.length/perPage));
        disPatch(setPartialArticles(partialData));
    }

    const handlePageSlected = e => {
        setOffset(e.selected * perPage);
        setPartialData();
    }

    return (
        <div className="articles-hide-siderbar">
            <div className="articles-row row">
                {isLoading === true ? (<div className='articles-spinner'>{
                    <Spinner animation="border" role="status">
                        <span className="sr-only">Loading...</span>
                    </Spinner>
                }</div>) : (
                <Fragment>{
                    partialArticles
                }</Fragment>)}
            </div>
            {isLoading === false && (
                <ReactPaginate
                previousLabel={"prev"}
                nextLabel={"next"}
                breakLabel={"..."}
                breakClassName={"break-me"}
                pageCount={pageCount}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={handlePageSlected}
                containerClassName={"pagination"}
                subContainerClassName={"pages pagination"}
                activeClassName={"active"}/>
            )}
            {isLoading === false && <Footer/>}
        </div>
    )
}

ArticleList.getInitialProps = async () => {
    let news;
    try {
        const disPatch = useDispatch();    
    } catch (err) {
      news = [];
    }

    return {
        news
    }
  }
export default ArticleList;
