import axios from 'axios'; //Types

import { GET_ARTICLES, GET_FILTERED_ARTICLES, SET_PARTIAL_ARTICLES, CLEAR_FILTER, SET_CATEGORY, CLEAR_CATEGORY, RESET_LOADING, SET_PLANNER, SET_PAPE_COUNT, SET_OFFSET, SET_LOADING, SET_LOGIN, SET_LOGOUT, SET_ACCESS_TOKEN } from '../types'; //Actions

export const getArticles = () => async dispatch => {
  var res = {
    data: []
  };

  try {
    res = await axios.get('/api/articles');
    await dispatch({
      type: GET_ARTICLES,
      payload: res.data
    });
  } catch (error) {
    //res.data = error;
    console.log(error);
  }
};
export const getFilteredArticles = text => dispatch => {
  dispatch({
    type: GET_FILTERED_ARTICLES,
    payload: text
  });
};
export const setPartialArticles = articles => dispatch => {
  dispatch({
    type: SET_PARTIAL_ARTICLES,
    payload: articles
  });
};
export const setLoading = isLoading => dispatch => {
  dispatch({
    type: SET_LOADING,
    payload: isLoading
  });
};
export const setOffset = offset => dispatch => {
  dispatch({
    type: SET_OFFSET,
    payload: offset
  });
};
export const setPageCount = pageCount => dispatch => {
  dispatch({
    type: SET_PAPE_COUNT,
    payload: pageCount
  });
};
export const clearFilter = () => dispatch => {
  dispatch({
    type: CLEAR_FILTER
  });
};
export const setLogin = () => dispatch => {
  dispatch({
    type: SET_LOGIN
  });
};
export const setLogout = () => dispatch => {
  dispatch({
