import { Action } from 'redux';
import { RootState} from '../store/store'
import axios from 'axios';
import { ThunkAction, ThunkDispatch } from 'redux-thunk'
import { AnyAction } from 'redux';
//Types
import { GET_ARTICLES, ArticleType } from '../types'

//Actions

interface GetArticlesAction {
    readonly type: typeof GET_ARTICLES
    readonly payload: Array<object>
}

export interface FetchTasksAction extends Action<'GET_ARTICLES'> {
    payload: ArticleType[];
}

export const getArticles = ():ThunkAction<Promise<FetchTasksAction>, {}, {}, FetchTasksAction> => {
    // Invoke API
    return async (dispatch: ThunkDispatch<{}, {}, FetchTasksAction>): Promise<FetchTasksAction>=> {
        try {

        const res = await axios.get('http://localhost:8000/api/articles');
        console.log(res.data)
        return dispatch({
            type: 'GET_ARTICLES', 
            payload: res.data})
        }
        catch (err) {}
    }
}

